angular.module("iColabora").controller("formController",function(a){a.materiais=[],a.insumos=[],a.adicionaMaterial=function(o){a.materiais.push(angular.copy(o)),delete a.material},a.adicionaInsumo=function(o){a.insumos.push(angular.copy(o)),delete a.insumo},a.$watchCollection("materiais",function(){a.totalMateriais=0,angular.forEach(a.materiais,function(o,t){a.totalMateriais+=o.quantidade*o.preco})}),a.$watchCollection("insumos",function(){a.totalInsumos=0,angular.forEach(a.insumos,function(o,t){a.totalInsumos+=o.quantidade*o.preco})}),a.totalDoPedido+=a.totalInsumos+a.totalMateriais});